import React from "react";
import "../../styles/tour-details.css";
import { Container, Row, Col, Form, ListGroup } from "reactstrap";
import { useParams } from "react-router-dom";
import tourData from "../../assets/data/tours";
import calculateAvgRating from "../../utils/avgRating";
const TourDetails = () => {
  const { id } = useParams();
  //this is an static data later we will call our API and load our data from DB
  const tour = tourData.find((tours) => tours.id === id);

  const { photo, title, desc, price, address,reviews, city, distance, maxGropSize } =
    tour;
    const {totalRating,avgRating}=calculateAvgRating()
  return (
    <>
    <section>
      <Container>
        <Row>
          <Col lg='8'>
            <div className="tour_content">
              <img src={photo} alt="" />

              <div className="tour_info">
                <h2>{title}</h2>

                <div className="d-felx align-items-center gap-5">
                <span className="tour_rating d-flex align-items-center gap-1">
              <i className="ri-star-fill"></i>
              {calculateAvgRating === 0 ? null : avgRating}
              {totalRating === 0 ? (
                "Not rated"
              ) : (
                <span>({reviews.length})</span>
              )}
            </span>
                </div>
              </div>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
    </>
  )
};

export default TourDetails;
